package br.com.examplesjava.csv;import com.opencsv.CSVWriter;import java.io.IOException;import java.io.Writer;import java.nio.file.Files;import java.nio.file.Paths;import java.util.ArrayList;import java.util.List;public class CreateCsvWithStringOpenCsv {  public static void main(String[] args) throws IOException {    String[] header = {"name", "age", "email"};    List<String[]> lines = new ArrayList<>();    lines.add(new String[] {"Joao", "35", "joao@dicasdejava.com.br"});    lines.add(new String[] {"Maria", "23", "maria@dicasdeprogramacao.com.br"});    lines.add(new String[] {"Ana", "25", "ana@dicasdejava.com.br"});    Writer writer = Files.newBufferedWriter(Paths.get("people.csv"));    CSVWriter csvWriter = new CSVWriter(writer);    csvWriter.writeNext(header);    csvWriter.writeAll(lines);    csvWriter.flush();    writer.close();  }}