package br.com.examplesjava.util.comand;public class GapBinario {  public static void main(String[] args) {    int value = solution(67);    System.out.println("Solution => " + value);  }  public static int solution(int N) {    while ((N & 1) == 0) {      N = N >> 1;    }    int max = 0;    while (N > 1) {      int count = -1;      do {        N = N >> 1;        count++;      } while ((N & 1) == 0);      if (max < count) {        max = count;      }    }    return max;  }}