package br.com.examplesjava.util.comand;import java.util.HashMap;import java.util.Map;public class MinNotIn {  public static void main(String[] args) {    int value = solution(new int[] {1, 2, 3});    System.out.println("Solution => " + value);  }  public static int solution(int[] A) {    Map<Integer, Integer> mapa = new HashMap<>();    for (int i = 0; i < A.length; i++) {      Integer count = mapa.get(A[i]);      if (count == null) {        count = 0;      }      mapa.put(A[i], count + 1);    }    for (int i = 1; i <= A.length; i++) {      Integer count = mapa.get(i);      if (count == null) {        return i;      }    }    return A.length + 1;  }}