package br.com.examplesjava.util.comand;public class StaticOrInstanceVariable {  static int variableStatic = 0;  int variableOfInstance = 0;  public static void main(String[] args) {    StaticOrInstanceVariable v1 = new StaticOrInstanceVariable();    v1.printStaticVariable("v1");    v1.printInstanceVariable("v1");    v1.variableStatic++;    v1.variableOfInstance++;    v1.printInstanceVariable("v1");    StaticOrInstanceVariable v2 = new StaticOrInstanceVariable();    v2.printInstanceVariable("v2");    v2.printStaticVariable("v2");    v1.variableStatic++;    v2.variableStatic++;    v2.variableOfInstance++;    v1.printInstanceVariable("v1");    v1.printStaticVariable("v1");    v2.printInstanceVariable("v2");    v2.printStaticVariable("v2");    StaticOrInstanceVariable.variableStatic++;    v1.printInstanceVariable("v1");    v1.printStaticVariable("v1");    v2.printInstanceVariable("v2");    v2.printStaticVariable("v2");  }  void printStaticVariable(String varClass) {    System.out.println("variableStatic => " + varClass + " - " + variableStatic);  }  void printInstanceVariable(String varClass) {    System.out.println("variableOfInstance => " + varClass + " - " + variableOfInstance);  }}