package br.com.examplesjava.util.files;import java.util.Base64;public class SerializationDeserializationBase64 {  public static void main(String[] args) {    String originalText = "this is a test string for Base64 serialization/deserialization";    System.out.println("Original text: " + originalText);    String textSerialization = Base64.getEncoder().encodeToString(originalText.getBytes());    System.out.println("Text in Base64: " + textSerialization);    String textDeserialization = new String(Base64.getDecoder().decode(textSerialization));    System.out.println("Deserialized text: " + textDeserialization);  }}