package br.com.examplesjava.util.files;import org.apache.commons.codec.binary.Base64;public class SerializationDeserializationBase64ApacheCommons {  public static void main(String[] args) {    String originalText = "this is a test string for Base64 serialization/deserialization";    System.out.println("Original text: " + originalText);    Base64 base64 = new Base64();    String textSerialization = base64.encodeAsString(originalText.getBytes());    System.out.println("Text in Base64: " + textSerialization);    String textDeserialization = new String(base64.decode(textSerialization));    System.out.println("Deserialized text: " + textDeserialization);  }}